<script lang="ts" setup>
import { Chat } from '@/modules';
import { useLiveStore } from '@/stores/live';
import { addLikeCount } from 'live-service';
import { computed } from 'vue';

const liveStore = useLiveStore()
const live = computed(() => liveStore.live)
const liveId = computed(() => live.value?.liveId)

const sendGift = async (gift: string) => {
    Chat.sendGift(gift)
    const res = await addLikeCount(liveId.value!)
    if (res.success && res.data) {
        liveStore.setLive({ like_count: res.data.likeCount })
    }
}
</script>

